import React from 'react'
import './index.css'
import { TabBar } from 'antd-mobile'
import { useNavigate,useLocation,Outlet } from 'react-router-dom'
export default function HomePages() {
  React.useEffect(()=>{
    const TOKEN_NAME = 'hkzf_city'


const getCurrentCity = ()=>{
  // 获取本地存储的城市
  const localCity = localStorage.getItem(TOKEN_NAME)
  if(!localCity){
    return new Promise((resolve,reject)=>{
      // 本地存储信息不存在时通过定位获取城市信息
      const currentCity = new window.BMapGL.LocalCity();
      currentCity.get(async (res)=>{
        try{
          const result = await axios.get(`/area/info?name=${res.name}`)
          localStorage.setItem(TOKEN_NAME,JSON.stringify(result.data.body))
          resolve(result.data.body)
        }catch(e){
          reject(e)
        }
      })
      // currentCity.get()
    })
  }else{
    return Promise.resolve(localCity)
  }
}
  })
  return (
    <>
      <div className="homePage">
        {/* 二级路由位置 */}
        <Outlet/>
      </div>
      <Bottom/>
    </>
  )
}
const Bottom = ()=>{
  const navigate = useNavigate();
  const location = useLocation();
  const {pathname} = location;
  console.log(pathname)
  const tabs = [
    {
      key:'/home',
      title:'首页',
      icon:<i className='iconfont icon-ind'></i>
    },
    {
      key:'/home/search',
      title:'找房',
      icon:<i className='iconfont icon-findHouse'></i>
    },
    {
      key:'/home/news',
      title:'资讯',
      icon:<i className='iconfont icon-infom'></i>
    },
    {
      key:'/home/profile',
      title:'我的',
      icon:<i className='iconfont icon-my'></i>
    }
  ]
  const setActiveKey = (value)=>{
    console.log(value);
    navigate(value)
  }
  return(
    <TabBar activeKey={pathname} onChange={setActiveKey}>
      {
        tabs.map((tab) => {
          return <TabBar.Item key={tab.key} icon={tab.icon} title={tab.title}/>
        })
      }
    </TabBar>
  )
}
