import React,{useState,useEffect} from 'react'
import { Swiper } from 'antd-mobile'
import SearchBars from '../../../components/SearchBar/index.jsx'
import { useCity } from '../../../utils/city'
import { axiosAPI as axios } from '../../../utils'
import {BASE_URL} from '../../../utils'

import Nav1 from '../../../asserts/images/nav-1.png'
import Nav2 from '../../../asserts/images/nav-2.png'
import Nav3 from '../../../asserts/images/nav-1.png'
import Nav4 from '../../../asserts/images/nav-1.png'

export default function Home() {
  const [cityLabel,cityValue] = useCity()
  useEffect(()=>{

  })
  return (
    <>
      <div className='swipers'>
        <Swipers/>
        <SearchBars city={cityValue}/>
      </div>

      <Nav/>
    </>
  )
}

// 轮播图组件
const Swipers = ()=>{
  const [swipersImg,setSwipersImg] = useState([])
  const [swipersLoaded,setsSwipersLoaded] = useState(false)
  useEffect (()=>{
    console.log('ik')
    const getSwiperImgs = async ()=>{
      const res = await axios.get('/home/swiper')
      console.log(res.data.body)
      setSwipersImg(res.data.body)
      setsSwipersLoaded(true)
    }
    getSwiperImgs()
    // 卸载组件时取消加载状态
    return()=>{
      setsSwipersLoaded(false)
    }
  },[])
  const swipers = 
    swipersImg.map((img)=>{
      return(
        <Swiper.Item key={img.id}>
          <div>
            <img src={`${BASE_URL}${img.imgSrc}`} alt='' style={{width:'100%'}}/>
          </div>
        </Swiper.Item>
      )
    })
  
  return (
    setsSwipersLoaded && <Swiper autoplay={true} loop={true}>{swipers}</Swiper>
  )
}

// 导航区域
const Nav = ()=>{
  const navs = [
    {
      id:1,
      img:Nav1,
      title:"整租",
      key:'/home/search'
    },
    {
      id:1,
      img:Nav2,
      title:"合租",
      key:'/home/search'
    },
    {
      id:1,
      img:Nav3,
      title:"地图找房",
      key:'/home/search'
    },
    {
      id:1,
      img:Nav4,
      title:"去出租",
      key:'/home/search'
    }
  ]
}
