      result.data.body.forEach((item)=>{
      })
    }
    renderMap()
    // 覆盖物渲染函数  判断渲染级别
    const createOverLays = ()=>{

    }
  })
  return (
    <div className="map">
      {/* 地图容器 */}
      <div id="container"></div>
    </div>
  )
}

// 设置缩放级别函数
const getTypeAndZoom = (map)=>{
  const zoom = map.getZoom()
  let nextZoom,type
  if(zoom>=10 && zoom<11){
    nextZoom = 13
    type = 'circle'
  }else if (zoom >= 11 && zoom < 15) {
    nextZoom = 15
    type = 'circle'
} else if (zoom >= 14 && zoom < 16) {
    type = 'rect'
}

  return {nextZoom,type}
}
