import React,{useState,useEffect} from 'react'
import './index.css'
import { Swiper } from 'antd-mobile'
import { SearchBar } from 'antd-mobile'
import { useCity } from '../../../utils/city'
import { axiosAPI as axios } from '../../../utils'
import {BASE_URL} from '../../../utils'
export default function Home() {
  const [cityList,setCityList] = useCity()
  return (
    <>
      <div className="swipers">
        <Swipers/>
        <SearchBar/>
      </div>
    </>
  )
}

// 轮播图组件
const Swipers = ()=>{
  const [swipersImg,setSwipersImg] = useState([])
  useEffect (()=>{
    console.log('ik')
    const getSwiperImgs = async ()=>{
      const res = await axios.get('/home/swiper')
      console.log(res.data.body)
      setSwipersImg(res.data.body)
    }
    getSwiperImgs()
  },[])
  return (
    <Swiper>
      {
        swipersImg.map((img)=>{
          return(
            <Swiper.Item key={img.id}>
              <div>
                <img src={`${BASE_URL}${img.imgSrc}`} alt='' style={{width:'100%'}}/>
              </div>
            </Swiper.Item>
          )
        })
      }
    </Swiper>
  )
}
